import React from "react";

import { styles } from "./styles";
import { useSelectTraining } from "./hooks";
import { Text, TextInput, TouchableOpacity, View } from 'react-native';
// import { Button } from "../../components/Form/Button";
import { PageContainer } from "../../components/PageContainer";
import { Card } from "../../components/Card";
import Ionicons from '@expo/vector-icons/Ionicons';
import { THEME } from "../../theme";

export function SelectTraining() {
  const { list, title, setCurrentTraining } = useSelectTraining();

  const searchIcon = <Ionicons name="search-outline" size={24} color={THEME.COLORS.PRIMARY} />

  return (
    <PageContainer>

      <Text style={styles.title}>
        Treino {title}
      </Text>

      <View style={styles.form}>
        <TextInput
          placeholder="Pesquisar em treinos..."
          style={styles.input}
        />
        <TouchableOpacity style={styles.searchButton}>
          {searchIcon}
        </TouchableOpacity>
      </View>

      <View>
        {list.map((training, trainingIndex) => (
          <Card
            key={`training_${trainingIndex}`}
            title={training.title}
            onPress={() => setCurrentTraining(training)}
          />
        ))}
      </View>

    </PageContainer>
  );
}
