import React, { useRef, useState } from 'react';
import { TextInput, TouchableOpacity, View } from 'react-native';
import Ionicons from '@expo/vector-icons/Ionicons';

import { focused, styles } from './styles';
import { THEME } from '../../theme';

export function SearchForm() {
    const [isFormFocused, setIsFormFocused] = useState(false);
    const searchIcon = <Ionicons name="search-outline" size={24} color={ isFormFocused ? THEME.COLORS.GRAY_200 : THEME.COLORS.PLACEHOLDER} />

    return (
        <View style={isFormFocused ? focused.form : styles.form}>
            <TextInput
                placeholder="Pesquisar em treinos..."
                placeholderTextColor={THEME.COLORS.PLACEHOLDER}
                onFocus={() => setIsFormFocused(true)}
                onBlur={() => setIsFormFocused(false)}
                style={styles.input}
            />
            <TouchableOpacity style={isFormFocused ? focused.searchButton : styles.searchButton}>
                {searchIcon}
            </TouchableOpacity>
        </View>
    );
}
