import React from "react";
import { Text, TouchableOpacity } from "react-native";

import { styles } from "./styles";
import { useSelectDivisions } from "./hooks";
// import { Button } from "../../components/Form/Button";
import { PageContainer } from "../../components/PageContainer";
import Ionicons from '@expo/vector-icons/Ionicons';
import { THEME } from "../../theme";
import { ListContainer } from "../../components/ListContainer";


export function SelectDivisions() {
  const { list, setCurrentDivision } = useSelectDivisions();

  const questionIcon = <Ionicons name="help-circle-outline" size={16} color={THEME.COLORS.BACKGROUND} />

  return (
    <PageContainer>

      {list.map((division, divisionIndex) => (
        <ListContainer>
          <TouchableOpacity key={`division_${divisionIndex}`} style={styles.card}>
            <Text>{division.title}</Text>
            {questionIcon}
          </TouchableOpacity>
        </ListContainer>
        // <Button
        //   key={`division_${divisionIndex}`}
        //   onPress={() => setCurrentDivision(division)}
        //   justifyContent="space-between"
        //   content={
        //     <>
        //       {division.title}

        //       <Image style={styles.icon} source={{ uri: questionImage }} />
        //     </>
        //   }
        // />
      ))}
    </PageContainer>
  );
}
