import React from 'react';
import { Text, TouchableOpacity, View } from 'react-native';

import Ionicons from '@expo/vector-icons/Ionicons';

import { styles } from './styles';
import { THEME } from '../../theme';

interface Props {
    hasBackButton?: boolean
}

export function Header({ hasBackButton = true }: Props) {
    const backArrowIcon = <Ionicons name="arrow-back" size={10} color={THEME.COLORS.PLACEHOLDER} />
    const logoutIcon = <Ionicons name="log-out-outline" size={10} color={THEME.COLORS.PLACEHOLDER} />

    return (
        <View style={styles.container}>
            <View style={styles.content}>
                {hasBackButton
                    && <TouchableOpacity style={styles.button}>
                        {backArrowIcon}
                    </TouchableOpacity>
                }

                <Text style={styles.text}>WEGOJIM</Text>

                <TouchableOpacity style={styles.button}>
                    {logoutIcon}
                </TouchableOpacity>
            </View>
        </View>
    );
}
